select cl.ext_tid, date(cl.date) as date from clicks_log cl left join conv_log co on cl.id=co.clicks_log_id left join campaigns c on c.id=cl.campaigns_id left join opportunities o on o.id=c.opportunities_id left join regions r on r.id=o.regions_id left join finance_entities fi on fi.id=r.finance_entities_id WHERE date(cl.date)>'2016-09-28' and fi.id=23 and co.id IS NOT NULL;