select date(cl.date) as date,cl.tid AS tid, (CASE WHEN co.id IS NOT NULL THEN 1 ELSE 0 END) AS has_conv, p.name AS traffic_source from clicks_log cl left join conv_log co on cl.id=co.clicks_log_id left join campaigns c on c.id=cl.campaigns_id left join opportunities o on o.id=c.opportunities_id left join regions r on r.id=o.regions_id left join finance_entities fi on fi.id=r.finance_entities_id left join providers p on p.id=cl.providers_id WHERE date(cl.date)>='2016-10-18' and date(cl.date)<='2016-10-25' and fi.id=23 ORDER BY cl.date;